// --- Task Management Brain ---

/**
 * Adds a new task to the list with basic validation
 * @param {string} title 
 */
function addTask() {
    // @logigo: Get Input Value
    const input = document.getElementById('task-input');
    const title = input.value.trim();

    // @logigo: Validate Title
    if (title === "") {
        alert("Please enter a task title!");
        return;
    }

    // @logigo: Create Task Object
    const task = {
        id: Date.now(),
        title: title,
        completed: false,
        timestamp: new Date().toLocaleTimeString()
    };

    // @logigo: Update UI List
    const taskList = document.getElementById('task-list');
    const li = document.createElement('li');
    li.innerHTML = `
        <span>${task.title}</span>
        <small style="color: #64748b; margin-left: 10px;">${task.timestamp}</small>
    `;

    // @logigo: Append and Clear
    taskList.appendChild(li);
    input.value = "";

    console.log("Task Added Successfully:", task.id);
}

// Event Listeners
document.getElementById('add-btn').addEventListener('click', addTask);

// Support Enter Key
document.getElementById('task-input').addEventListener('keypress', (e) => {
    if (e.key === 'Enter') addTask();
});
