/**
 * PATHWAY DEMO: Dijkstra's shortest path algorithm.
 */
function optimizeRoute(cities, start) {
  const dists = {};
  const seen = new Set();
  const backlog = Object.keys(cities);
  backlog.forEach(city => dists[city] = Infinity);
  dists[start] = 0;
  while (seen.size < backlog.length) {
    let best = null;
    let minD = Infinity;
    backlog.forEach(city => {
      if (!seen.has(city) && dists[city] < minD) {
        best = city;
        minD = dists[city];
      }
    });
    if (best === null) break;
    seen.add(best);
    for (const [neighbor, cost] of Object.entries(cities[best])) {
      const newD = dists[best] + cost;
      if (newD < dists[neighbor]) {
        dists[neighbor] = newD;
      }
    }
  }
  return dists;
}
